import { Component } from "react"
import "./Contact.css"

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      message: ''
    }
  }

  render() {
    return (
      <div id="contact">
        <hr/>

        <div className="center">
          <h1>Contact</h1>

          <p style={{ paddingTop: "15px" }}>You can email me at shrader.gavin@gmail.com</p>
          <p style={{ paddingTop: "15px", color: "red" }}><i>Note: This form isn't working yet.</i></p>
        </div>

        <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input type="text" className="form-control" value={this.state.name} onChange={this.onNameChange.bind(this)} />
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">Email address</label>
            <input type="email" className="form-control" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)} />
          </div>
          <div className="form-group">
            <label htmlFor="message">Message</label>
            <textarea className="form-control" rows="5" value={this.state.message} onChange={this.onMessageChange.bind(this)} />
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>

        <form className="cf" id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
          <div className="half left cf">
            <input type="text" id="input-name" placeholder="Name" />
            <input type="email" id="input-email" placeholder="Email address" />
            <input type="text" id="input-subject" placeholder="Subject" />
          </div>
          <div className="half right cf">
            <textarea name="message" type="text" id="input-message" placeholder="Message"></textarea>
          </div>
          <input type="submit" value="Submit" id="input-submit" />
        </form>
      </div>
    );
  }

  onNameChange(event) {
    this.setState({name: event.target.value})
  }

  onEmailChange(event) {
    this.setState({email: event.target.value})
  }

  onMessageChange(event) {
    this.setState({message: event.target.value})
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
  }
}

export default Contact;
