import { Component } from "react"
import "./Contact.css"

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      message: ''
    }
  }

  render() {
    return (
      <div id="contact">
        <hr/>
          <h1>Contact</h1>

          <p style={{ paddingTop: "25px" }}>You can email me at shrader.gavin@gmail.com</p>

          <p style={{ paddingTop: "15px", color: "red" }}>Note: This form isn't working yet (and it looks like trash lol - styling in the works): </p>

          <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
            <div>
              <label htmlFor="name">Name</label>
              <input type="text" />
            </div>
            <div>
              <label htmlFor="exampleInputEmail1">Email address</label>
              <input type="email" aria-describedby="emailHelp" />
            </div>
            <div>
              <label htmlFor="message">Message</label>
              <textarea rows="5"></textarea>
            </div>
            <button type="submit">Submit</button>
          </form>
      </div>
    );
  }

  onNameChange(event) {
    this.setState({name: event.target.value})
  }

  onEmailChange(event) {
    this.setState({email: event.target.value})
  }

  onMessageChange(event) {
    this.setState({message: event.target.value})
  }

  handleSubmit(event) {
  }
}

export default Contact;
