/* https://reactrouter.com/docs/en/v6/getting-started/tutorial */

import { Outlet, NavLink } from "react-router-dom";
import { HashLink } from 'react-router-hash-link';
import React, { useState, useEffect, useRef } from "react";
import './App.css';

function App() {
  const [navListState, setNavListState] = useState('navListClosedNoTransition');
  const drawerRef = useRef(null);

  useEffect(() => {
    /* If the screen resizes beyond our breakpoint, close the nav bar */
    const handleResize = () => {
      if (window.innerWidth > 720) {
        setNavListState('navListClosedNoTransition')
        document.body.style.overflow = "visible";
      }
    }

    window.addEventListener("resize", handleResize);

    /* Close the drawer when the user clicks outside of it */
    const closeDrawer = (event) => {
      if (drawerRef.current && drawerRef.current.contains(event.target)) {
        return;
      }

      setNavListState('navListClosed');
      document.body.style.overflow = "visible";
    };

    document.addEventListener("mouseup", closeDrawer);
    return () => {
      document.removeEventListener("mouseup", closeDrawer);
    }
  }, []);

  return (
    <div>
      <div className="navWrapper">
        <nav className="navigation">
          <h1 className="logo"><NavLink style={{ color: "black", textDecoration: "none" }} to="/">Gavin Shrader</NavLink></h1>

          <button className="hamburgerButton" onClick={() => {
            setNavListState('navListOpen')
            document.body.style.overflow = "hidden";
          }}>
            <div className="hamburgerLines"></div>
          </button>

          <ul ref={drawerRef} className={` navUl ${navListState}`}>
            <li className="navUlLi"><HashLink smooth to="/#home">Home</HashLink></li>
            <li className="navUlLi"><HashLink smooth to="/#skills">Skills</HashLink></li>
            <li className="navUlLi"><HashLink smooth to="/#contact">Contact</HashLink></li>
            <li className="navUlLi"><NavLink to="/projects">Projects</NavLink></li>
            <li className="navUlLi"><NavLink to="/resume">Resume</NavLink></li>
            <li className="navUlLi"><NavLink style={({ isActive }) => {return { color: isActive ? "red" : "" }; }} to="/blog">Blog</NavLink></li>
            <li className="navUlLi"><a href="https://github.com/gshrdr/" target="_blank" rel="noopener noreferrer">GitHub &rarr;</a></li>
            <li className="navUlLi liBottom">
              <button className="liExitButton" onClick={() => {
                setNavListState('navListClosed')
                document.body.style.overflow = "visible";
              }}>
                &laquo; Close
              </button>
            </li>
          </ul>
        </nav>
      </div>
      <Outlet />
    </div>
  );
}

export default App;
