import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";

class Starfield extends Component {

  componentDidMount() {
    // setup window size resizing
    const width = this.mount.clientWidth;
    const height = this.mount.clientHeight;
    window.addEventListener('resize', this.handleWindowResize);

    // setup camera
    var camera = new THREE.PerspectiveCamera( 70, window.innerWidth / window.innerHeight, 0.01, 10 );
    camera.position.z = 1;

    // setup scene
    var scene = new THREE.Scene();
    scene.background = new THREE.Color( 0x111827 );

    // setup rendering
	  var renderer = new THREE.WebGLRenderer( { antialias: true } );
	  renderer.setSize( window.innerWidth, window.innerHeight );
    this.mount.appendChild( renderer.domElement );

    // setup geometries
    var geometry = new THREE.BoxGeometry( 0.4, 0.4, 0.4 );
    var material = new THREE.MeshNormalMaterial();
    var mesh = new THREE.Mesh( geometry, material );
    scene.add( mesh );

    renderer.render( scene, camera );

    /*
    var animate = function (time) {
      requestAnimationFrame( animate );

      mesh.rotation.x = time / 2000;
	    mesh.rotation.y = time / 1000;

      //camera.aspect = window.clientWidth / window.clientHeight;
      camera.updateProjectionMatrix();

      renderer.setSize( window.innerWidth, window.innerHeight );
      renderer.setPixelRatio(window.devicePixelRatio);

	    renderer.render( scene, camera );
    }
    animate();*/
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleWindowResize);
    this.mount.removeChild(this.renderer.domElement);
  }

  handleWindowResize = () => {
    const width = this.mount.clientWidth;
    const height = this.mount.clientHeight;

    this.camera.aspect = width / height;
    this.camera.updateProjectionMatrix();

    this.renderer.setSize(width, height, false);
  };

  render() {
    return (
      <div className="starfield">
        <div ref={ref => (this.mount = ref)} />
      </div>
    );
  }
}

export default Starfield;
