import { TerminalIcon, CodeIcon } from "@heroicons/react/solid";
import React from "react";
import { skills } from "../data";

export default function Skills() {
  return (
    <section id="skills">
      <div className="container px-5 py-10 mx-auto">
        <div className="text-center mb-20">
          <CodeIcon className="mx-auto inline-block w-10 mb-4" />
          <h1 className="sm:text-4xl text-3xl font-medium title-font text-white mb-4">
            Skills &amp; Technologies
          </h1>
          <p className="text-base leading-relaxed xl:w-2/4 lg:w-3/4 mx-auto">
            Lorem ipsum dolor sit amet consectetur, adipisicing elit. Nisi sit
            ipsa delectus eum quo voluptas aspernatur accusantium distinctio
            possimus est.
          </p>
        </div>
        <div className="flex flex-wrap lg:w-4/5 sm:mx-auto sm:mb-2 -mx-2">
          {skills.map((skill) => (
            <div key={skill.skill} className="p-2 sm:w-1/2 w-full">
              <div className="bg-gray-800 rounded flex p-4 h-full items-center">

                {/*
                {
                  skill.color == "blue" ? <TerminalIcon className={"block w-8 text-blue-500 mb-4"} /> :
                  skill.color == "green" ? <TerminalIcon className={"block w-8 text-green-500 mb-4"} /> :
                  skill.color == "orange" ? <TerminalIcon className={"block w-8 text-orange-500 mb-4"} /> :
                  <TerminalIcon className={"block w-8 text-red-500 mb-4"} />
                }
                */}

                {/*
                <img
                  alt="testimonial"
                  src={testimonial.image}
                  className="w-12 rounded-full flex-shrink-0 object-cover object-center"
                />
                */}

                <span className="flex-grow flex flex-col pl-4">
                    <span className="flow-root">
                      <span className="float-left title-font font-medium text-white">
                        {skill.skill}
                      </span>
                      <span className="float-right pr-4 title-font font-medium text-white">
                        Test
                      </span>
                    </span>
                  <span className="text-gray-500 text-sm uppercase">
                    {
                      skill.url == null ? "" : <a className="hover:text-blue-600" href={skill.url}>{skill.urlName}</a>
                    }
                  </span>
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
